'use strict';

function extractorMdc() {
  return {
    name: "@unocss/extractor-mdc",
    async extract(ctx) {
      if (!/\.(md|mdc|markdown)$/i.test(ctx.id ?? ""))
        return;
      ctx.code.match(/\.[\w:\/_-]+/g)?.forEach((c) => {
        ctx.extracted.add(c.slice(1));
      });
    }
  };
}

module.exports = extractorMdc;
